import React, { useContext, useEffect, useReducer } from "react";
import { ShopContext } from "../../context/shop-context";
import { PRODUCTS } from "../../products";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";

import "./cart.css";

const ACTIONS = {
  SET_CART_ITEMS: "set-cart-items",
};

const cartReducer = (state, action) => {
  switch (action.type) {
    case ACTIONS.SET_CART_ITEMS:
      return action.payload;
    default:
      return state;
  }
};

export const Cart = () => {
  const [cartItems, dispatch] = useReducer(cartReducer, getDefaultCart());

  const { getTotalCartAmount, checkout } = useContext(ShopContext);
  const totalAmount = getTotalCartAmount();

  const navigate = useNavigate();

  useEffect(() => {
    const storedCartItems = JSON.parse(localStorage.getItem("cartItems"));
    if (storedCartItems) {
      dispatch({ type: ACTIONS.SET_CART_ITEMS, payload: storedCartItems });
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
  }, [cartItems]);

  function getDefaultCart() {
    let cart = {};
    for (let i = 1; i < PRODUCTS.length + 1; i++) {
      cart[i] = 0;
    }
    return cart;
  }

  function updateCartItemCount(newAmount, itemId) {
    dispatch({
      type: ACTIONS.SET_CART_ITEMS,
      payload: { ...cartItems, [itemId]: newAmount },
    });
  }

  function removeCartItem(itemId) {
    dispatch({
      type: ACTIONS.SET_CART_ITEMS,
      payload: { ...cartItems, [itemId]: 0 },
    });
  }

  return (
    <div className="cart">
      <div>
        <h1>Your Cart Items</h1>
      </div>
      <div className="cart">
        {PRODUCTS.map((product) => {
          if (cartItems[product.id] !== 0) {
            return (
              <CartItem
                key={product.id}
                data={product}
                count={cartItems[product.id]}
                updateCount={(newAmount) =>
                  updateCartItemCount(newAmount, product.id)
                }
                remove={() => removeCartItem(product.id)}
              />
            );
          }
        })}
      </div>

      {totalAmount > 0 ? (
        <div className="checkout">
          <p> Subtotal: ${totalAmount} </p>
          <button onClick={() => navigate("/shop")}> Continue Shopping </button>
          <button
            onClick={() => {
              checkout();
              navigate("/Billing");
            }}
          >
            {" "}
            Checkout{" "}
          </button>
        </div>
      ) : (
        <h1> Your Shopping Cart is Empty</h1>
      )}
    </div>
  );
};
