import React, { useContext, useEffect, useState } from "react";
import { ShopContext } from "../../context/shop-context";
import { PRODUCTS } from "../../products";


import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";
import { SET_CART_ITEMS } from "../../context/shop-context";
import { useDispatch } from "react-redux";

import "./cart.css";

export const Cart = () => {
  const { cartItems, getTotalCartAmount, checkout } = useContext(ShopContext);
  const totalAmount = getTotalCartAmount();

  const navigate = useNavigate();
  const [cartItemsState, setCartItemsState] = useState(cartItems);

  const updateCartItems = (newCartItems) => {
    localStorage.setItem("cartItems", JSON.stringify(newCartItems));
    setCartItemsState(newCartItems);
  };

  useEffect(() => {
    const storedCartItems = JSON.parse(localStorage.getItem("cartItems"));
    if (storedCartItems) {
      setCartItemsState(storedCartItems);
    }
  }, []);

  const addToCart = (itemId) => {
    const newCartItems = { ...cartItemsState, [itemId]: cartItemsState[itemId] + 1 };
    updateCartItems(newCartItems);
  };

  const removeFromCart = (itemId) => {
    const newCartItems = { ...cartItemsState, [itemId]: cartItemsState[itemId] - 1 };
    updateCartItems(newCartItems);
  };

  const updateCartItemCount = (newAmount, itemId) => {
    const newCartItems = { ...cartItemsState, [itemId]: newAmount };
    updateCartItems(newCartItems);
  };

  return (
      <div className="cart">
        <div>
          <h1>Your Cart Items</h1>
        </div>
        <div className="cart">
          {PRODUCTS.map((product) => {
            if (cartItemsState[product.id] !== 0) {
              return (
                  <CartItem
                      key={product.id}
                      data={product}
                      cartItemAmount={cartItemsState[product.id]}
                      addToCart={addToCart}
                      removeFromCart={removeFromCart}
                      updateCartItemCount={updateCartItemCount}
                  />
              );
            }
          })}
        </div>

        {totalAmount > 0 ? (
            <div className="checkout">
              <p> Subtotal: ${totalAmount} </p>
              <button onClick={() => navigate("/shop")}> Continue Shopping </button>
              <button
                  onClick={() => {
                    checkout();
                    navigate("/Billing");
                  }}
              >
                {" "}
                Checkout{" "}
              </button>
            </div>
        ) : (
            <h1> Your Shopping Cart is Empty</h1>
        )}
      </div>
  );
};
