import React, { useEffect, useState } from "react";
import "./HomePage.css";
import SimpleImageSlider from "react-simple-image-slider";

export default function HomePage() {
  const sliderImages = [
    {
      url: "images/hp1.jpg",
    },
    {
      url: "images/hp2.jpg",
    },
    {
      url: "images/16.jpg",
    },
  ];

  const images = [
    {
      url: "images/product1.jpg",
      alt: "Product 1",
    },
    {
      url: "images/product2.jpg",
      alt: "Product 2",
    },
    {
      url: "images/product3.jpg",
      alt: "Product 3",
    },
    {
      url: "images/product4.jpg",
      alt: "Product 4",
    },
    {
      url: "images/product5.jpg",
      alt: "Product 5",
    },
    {
      url: "images/product6.jpg",
      alt: "Product 6",
    },
  ];

  return (
    <div className="home">
      <header>
        <h1>MY SHOP</h1>
      </header>
      <div className="banner">
        <SimpleImageSlider
          width={1848}
          height={700}
          images={sliderImages}
          showNavs={true}
          slideDuration={0.8}
          autoPlay={true}
        />
      </div>
      <section className="products">
        <div className="row">
          {images.slice(0, 3).map((image, index) => (
            <div key={index} className="col">
              <img src={image.url} alt={image.alt} />
            </div>
          ))}
        </div>
        <div className="row">
          {images.slice(3).map((image, index) => (
            <div key={index} className="col">
              <img src={image.url} alt={image.alt} />
            </div>
          ))}
        </div>
      </section>
      <footer>
        <p>Â© 2023 My Shop. All rights reserved.</p>
      </footer>
    </div>
  );
}
