        </Route>

        <Route path="/confirm-payment">
          <h2>Confirm Payment</h2>
          <p>Selected payment method: {selectedPayment}</p>
          </Route>
          </Routes>
        </Router>
      </ShopContextProvider>
    </div>
  );
}
export default App;
