import React, { useEffect, useState } from 'react';
import './HomePage.css';
import SimpleImageSlider from 'react-simple-image-slider';

export default function App() {
  const sliderImages = [
    {
      url: 'images/hp1.jpg',
    },
    {
      url: 'images/hp2.jpg',
    },
    {
      url: 'images/16.jpg',
    },
  ];

  const testimonials = [
    {
      id: 1,
      text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Aenean id mauris semper, aliquam neque vel, vestibulum urna. Sed blandit ex et pulvinar posuere.",
      author: 'John Doe',
      jobTitle: 'CEO, Acme Inc.',
    },
    {
      id: 2,
      text: "Nulla facilisi. Fusce fringilla sapien est, eu interdum sem vehicula vitae. Duis eget elit arcu. Praesent blandit velit id dui sodales, nec consequat quam aliquet.",
      author: 'Jane Smith',
      jobTitle: 'Marketing Manager, XYZ Corp.',
    },
    {
      id: 3,
      text: 'Vestibulum ante ipsum primis in faucibus orci luctus et ultrices posuere cubilia curae; Curabitur non magna sed justo hendrerit ullamcorper. Pellentesque ac ultrices elit.',
      author: 'Bob Johnson',
      jobTitle: 'CTO, ABC Industries',
    },
  ];

  return (
    <div className="home">
      <h1>MY SHOP</h1>

      <div className="banner">
        <SimpleImageSlider
          width={1848}
          height={700}
          images={sliderImages}
          showNavs={true}
          slideDuration={0.8}
          autoPlay={true}
        />
        <h2>
          Let us help you reach your audience We are a digital marketing agency in Bhubaneswar that handles all your online broadcasting strategies for better reach to a broader range of audience. We communicate your business with most compelling marketing strategies and creative ideas.
        </h2>

        <div className="testimonials">
          <h3>What Our Customers Are Saying</h3>
          <div className="testimonial-list">
  {testimonials.map((testimonial) => (
    <div className="testimonial-box" key={testimonial.id}>
      <p>{testimonial.text}</p>
      <div className="testimonial-author">
        <strong>{testimonial.author}</strong>
        <span>{testimonial.jobTitle}</span>
      </div>
    </div>
  ))}
</div>

          </div>
        </div>

        <footer>
          <p>Â© 2023 My Shop. All rights reserved.</p>
        </footer>
      </div>

  );
}
